import React from "react";
import Fullpage, {FullPageSections, FullpageSection, FullpageNavigation} from '@ap.cx/react-fullpage'
import Homepage from "../Pages/Homepage";
import AboutMe from "../Pages/AboutMe";
import Projects from "../Pages/Projects";
import {Parallax, ParallaxLayer} from '@react-spring/parallax'


const FullpageScroll = () => {
    const SectionStyle = {
        height: '100vh',
        width: '100%',
        display: 'block',
        position: 'relative'
    }
    return (
        <>

            <Parallax pages={3}> 
                <Homepage style = {{top: '-0.16em', transition: 'padding .5s, top .35s, box-shadow .4s', boxShadow:'0 0.4rem 0 -0.2rem #0ebac5'}} />
                <ParallaxLayer sticky={{ end: 1.0}} style={{paddingTop: '80px'}} speed={3}>
                    <img src = "/Images/Subject.png" alt=""></img>
                </ParallaxLayer>
                <ParallaxLayer offset={1} speed={0.5}>
                            <AboutMe/>
                </ParallaxLayer>
                <ParallaxLayer offset={2} speed = {2.75} style={{backgroundColor: 'rgb(208, 207, 203)'}}>
                    <Projects/>
                </ParallaxLayer>
            </Parallax>
        </>
    )
}

export default FullpageScroll
